mapper.update:
update board set id = #{id},
				  title = #{title},
				  regdate = now(),
				  contents = #{contents}
where no = #{no}

mapper.select:
select * from board

	
		
			
				id = #{word}
			
			
				${key} like concat('%',#{word},'%')
			
		
	

order by no desc
limit #{startNo}, #{interval}

select count(*) from board

	
		
			
				id = #{word}
			
			
				${key} like concat('%',#{word},'%')
