mapper.update:
update member set password = #{password},
				  name = #{name},
				  email = #{email},
				  address = #{address},
				  phone = #{phone}
where id = #{id}

mapper.select:
select * from member

	
		
			
				id = #{word}
			
			
				${key} like concat('%',#{word},'%')
			
		
	

order by id desc
limit #{startNo}, #{interval}

select count(*) from member

	
		
			
				id = #{word}
			
			
				${key} like concat('%',#{word},'%')
